sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue == '') {
        return;
    }

    try {

        var start_date = getDateFromFormat(newValue, g_user_date_format);
        var start_date_stg = new Date(start_date);
        var start_date_formated = formatDate(start_date_stg, 'yyyyMMdd');

        var now_date = new Date();
        var now_date_formated = formatDate(now_date, 'yyyyMMdd');

        if (start_date_formated < now_date_formated) {
            g_form.clearValue('u_start_date');
            g_form.showErrorBox("u_start_date", "Start Date cannot be in past", true);

        } else
            g_form.hideFieldMsg('u_start_date', true);


        g_form.getValue('u_end_date') != '' && g_form.clearValue('u_end_date');

    } catch (err) {
        jslog('runtime error: {0}', err);
    }

}
