sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading) {
        return;
    }

    try {

        var end_date = new Date(newValue);
        var end_date_str = formatDate(end_date, g_user_date_time_format);
        var end_date_num = getDateFromFormat(end_date_str, g_user_date_time_format);

        var start_date = new Date(g_form.getValue('u_start_date'));
        var start_date_str = formatDate(start_date, g_user_date_time_format);
        var start_date_num = getDateFromFormat(start_date_str, g_user_date_time_format);

        if (end_date_num < start_date_num) {
            g_form.showErrorBox("u_end_date", "End Date must be after the Start date", true);
            //g_form.clearValue('u_end_date');
        } else {
            g_form.hideFieldMsg('u_end_date', true);
        }
    } catch (err) {
        g_form.addErrorMessage('runtime error: ' + err);
    }

}
