sys_script_include.script:
var DateValidationUtil = Class.create();
DateValidationUtil.prototype = {
    initialize: function(startDate, endDate) {
        this.DEBUG_PREFIX = "KIT.SN.SI.DateValidationUtil:";
        this.startDate = startDate;
        this.endDate = endDate;
    },

    /* checks if the date is in future */
    isFutureDate: function() {
        try {

            return this.startDate >= new GlideDate() ? true : false;

        } catch (err) {
            gs.error("{0} runtime error {1}", this.DEBUG_PREFIX, err);
            throw err;
        }
    },

    /* checks if the date is in past */
    isPastDate: function() {
        try {

            return this.startDate < new GlideDate() ? true : false;

        } catch (err) {
            gs.error("{0} runtime error {1}", this.DEBUG_PREFIX, err);
            throw err;
        }
    },

    /* checks if the end date is after the start date */
    isValidDateDiff: function() {
        try {

            return this.endDate >= this.startDate ? true : false;

        } catch (err) {
            gs.error("{0} runtime error {1}", this.DEBUG_PREFIX, err);
            throw err;
        }
    },

    type: 'DateValidationUtil'
};
