sys_ux_client_script.script:
/*
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    const status = api.data.gform.nowRecordFormBlob.formFieldValues.u_state.value;
    const {
        status: action
    } = event.payload;

    if (!action) {
        return;
    }

    if (status === '2')
        helpers.navigate.to('submitted', {
            sysId: event.payload.screenParams.sys_id,
            table: event.payload.screenParams.table
        });
    else if (status === '0')
        helpers.navigate.to('draft', {
            sysId: event.payload.screenParams.sys_id,
            table: event.payload.screenParams.table
        });


}
