sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const strTable = api.context.props.table;
    const strSysid = api.context.props.sysid;

    helpers.navigate.to('wfh-request', {
        page: 'edit',
        table: strTable,
        sysid: strSysid
    }, {
        refresh: Date.now()
    });
}
