sys_ux_data_broker_transform.script:
function transform(input) {
    var returnArray = [];
    var arrayTime = new global.ArrayUtil();
    var records = input.data;
    var namesOnly = [];
    for (var r in records) {
        namesOnly.push(records[r].u_user.displayValue);
    }
    namesOnly = arrayTime.unique(namesOnly);


    for (var i in namesOnly) {
        var myObj = {};
        myObj.id = namesOnly[i];
        myObj.title = namesOnly[i];
        myObj.showHeader = false;
        myObj.isCollapsed = false;

        returnArray.push(myObj);
    }

    return returnArray;
}
